CREATE TABLE challenge_phone (
	_id INT NOT NULL PRIMARY KEY AUTO_INCREMENT COMMENT 'Phone key',
	name VARCHAR(255) NOT NULL COMMENT 'Phone name',
	description TEXT DEFAULT NULL COMMENT 'Notes about phone',
	image TEXT DEFAULT NULL COMMENT 'URL to iamge',
	price INT NOT NULL COMMENT 'Price in Euro cents'
) DEFAULT CHARACTER SET utf8 COLLATE utf8_bin ENGINE = InnoDB;

CREATE TABLE challenge_order (
	_id INT NOT NULL PRIMARY KEY AUTO_INCREMENT COMMENT 'Order key',
	customer_name VARCHAR(255) NOT NULL COMMENT 'Customer name',
	customer_surname VARCHAR(255) NOT NULL COMMENT 'Customer surname',
	customer_email VARCHAR(255) NOT NULL COMMENT 'Customer email',
	phones VARCHAR(255) NOT NULL COMMENT 'Phones IDs comma sparated',
	total_price INT NOT NULL COMMENT 'Total price',
	created_at DATETIME NOT NULL DEFAULT NOW() COMMENT 'Creation date' 
) DEFAULT CHARACTER SET utf8 COLLATE utf8_bin ENGINE = InnoDB;

